class Hello{

    def log;
    def context;
    def testRunner;

    def Hello(log, context, testRunner)
    { 
    	   this.log =log
    	   this.context = context
    	   this.testRunner = testRunner

    }

    def sayHello( String name){

    	  log.info("   Saying Hello to:   "+ name)

    }

}
context.setProperty("Hello",new Hello(log,context,testRunner))

def testStep= testRunner.testCase.getTestStepByName("Hello")
testStep.run(testRunner,context)
context.Hello.sayHello("Hema")  //to get access of the method Hello

project = testRunner.testCase.testSuite.project ;
 tcase = project.testSuites["TestSuite3"].testCases["CallFromSameMethod"] ; 
 hello = tcase.getTestStepByName("Hello");  //Get access of step Step Hello
hello.run(testRunner,context)
context.Hello.sayHello("Hema")

